import java.util.*;public class Word {    /*    thanh phan word     */    private String word_target,                   word_explain;    /*    ham constructor     */    public Word(String word, String explain){        this.word_target = word;        this.word_explain = explain;    }    /*    ham lay tu word     */    public String getWord(){        return this.word_target;    }    /*    ham lay nghia tu     */    public String getExplain(){        return this.word_explain;    }}